mapper.select:
SELECT SEAL_TYPE, SEAL_SIZE, SEAL_CONFIG,
              TEMP_NOR, TEMP_MIN, TEMP_MAX,
              SPEC_GRAVITY_NOR,SPEC_GRAVITY_MIN,SPEC_GRAVITY_MAX,
              VISC_NOR,VISC_MIN,VISC_MAX,
              SEAL_CHAM_NOR,SEAL_CHAM_MIN,SEAL_CHAM_MAX
FROM M_DATA_CNV2_1
WHERE 1=1
AND TEMP_NOR=#{TEMP_NOR}
AND TEMP_MIN=#{TEMP_MIN}
AND TEMP_MAX=#{TEMP_MAX}
AND SPEC_GRAVITY_NOR=#{SPEC_GRAVITY_NOR}
AND SPEC_GRAVITY_MIN=#{SPEC_GRAVITY_MIN}
AND SPEC_GRAVITY_MAX=#{SPEC_GRAVITY_MAX}
AND VISC_NOR=#{VISC_NOR}
AND TEMP_NOR=#{TEMP_NOR}
AND VISC_MIN=#{VISC_MIN}
AND VISC_MAX=#{VISC_MAX}
AND SEAL_CHAM_NOR=#{SEAL_CHAM_NOR}
AND SEAL_CHAM_MIN=#{SEAL_CHAM_MIN}
AND SEAL_CHAM_MAX=#{SEAL_CHAM_MAX}

mapper.insert:
INSERT INTO TB_ML_MODEL_ATTR ( MODEL_ID,ATTR_TYPE,SEQ,V_VAL1, CREATE_DT,CREATE_USER,UPDATE_DT,UPDATE_USER ) SELECT '${MODEL_ID}', '${ATTR_TYPE}', ROW_NUMBER() OVER(ORDER BY CLASS ASC), CLASS, GETDATE(), #{USER_ID}, GETDATE(), #{USER_ID} FROM ( SELECT DISTINCT SEAL_TYPE as CLASS API_PLAN as CLASS SEAL_TYPE+' | '+MATERIAL+' | '+API_PLAN as CLASS FROM M_DATA_CNV2_1 WHERE EQUIP_TYPE != 'Comp.' AND SEAL_TYPE !='-' AND API_PLAN !='-'
AND SEAL_TYPE !='-' AND MATERIAL !='-' AND API_PLAN !='-'
) A ORDER BY A.CLASS

mapper.update:
UPDATE TB_ML_MODEL_MNG
SET TITLE=#{TITLE}
WHERE MODEL_ID=#{MODEL_ID}

mapper.select:
SELECT V_VAL1 as V_VAL1
FROM TB_ML_MODEL_ATTR
WHERE MODEL_ID=#{MODEL_ID}
AND ATTR_TYPE=#{ATTR_TYPE}
ORDER BY SEQ

SELECT MAX(MODEL_ID) as MODEL_ID
FROM TB_ML_MODEL_MNG
WHERE ATTR1 =#{ATTR1}

SELECT *
FROM TB_ML_MODEL_MNG
WHERE MODEL_ID=#{MODEL_ID}
AND MODEL_SUB_TYPE =#{CLASS_TYPE}

SELECT V_VAL1 as GRP, V_VAL2 as GRP_SUB
FROM TB_ML_MODEL_ATTR
WHERE MODEL_ID=#{MODEL_ID}
AND ATTR_TYPE=#{ATTR_TYPE}
ORDER BY LEN(V_VAL2) desc

SELECT V_VAL1 as LV1, V_VAL2 as LV2, V_VAL3 as LV3
FROM TB_ML_MODEL_ATTR
WHERE MODEL_ID=#{MODEL_ID}
AND ATTR_TYPE=#{ATTR_TYPE}
ORDER BY 1,2,3

